<style lang="scss">
@mixin sizeRem($size) {
  font-size: calc($size / 16) * 1rem;
}
.footer {
  padding-top: 40px;
  padding-bottom: 40px;
  &__inner {
    display: flex;
    justify-content: space-between;
  }
  &__copyright {
    p {
      // margin-bottom: 0;
      color: #666;
      @include sizeRem(14);
    }
    a {
      color: #444;
      &:hover {
        color: var(--color-primary);
      }
    }
  }
  &__social {
    ul {
      display: flex;
      padding-left: 0;
    }
    li {
      list-style-type: none;
    }
    a {
      display: flex;
      flex-direction: column;
      text-align: center;
      padding: 0 20px;
      color: #666;
      text-decoration: none;
      svg {
        width: 18px;
      }
      span {
        font-size: 14px;
      }
      &:hover {
        color: var(--color-primary);
      }
    }
  }
  &__qr {
    ul {
      display: flex;
      padding-left: 20px;
    }
    li {
      display: flex;
      flex-direction: column;
      text-align: center;
      padding: 0 10px;
      list-style-type: none;
      font-size: 12px;
    }
    i {
      display: inline-block;
      margin-bottom: 8px;
    }
    svg {
      padding: 8px;
      background-color: #fff;
      border-radius: 6px;
      box-shadow: 0 1px 2px rgba($color: #000, $alpha: .25);
    }
    a {
      font-weight: 700;
      text-decoration: none;
      &:hover {
        text-decoration: underline;
      }
    }
    span {
      color: #666;
    }
  }
  /** Media **/
  @media (max-width: 767px) {
    padding-top: 30px;
    padding-bottom: 20px;
    background-color: rgba($color: #000, $alpha: 0.05);
    &__inner {
      flex-direction: column-reverse;
    }
    &__copyright {
      p {
        color: #444;
        @include sizeRem(12);
      }
    }
    &__qr {
      ul {
        position: relative;
        justify-content: center;
        padding-left: 0;
        padding-bottom: 30px;
        margin-bottom: 30px;
        // border-bottom: 1px dashed rgba($color: #000, $alpha: .25);
        &::after {
          content: '';
          position: absolute;
          left: 50%;
          bottom: 0;
          height: 0;
          width: 100px;
          transform: translate3d(-50%,0,0);
          border-top: 1px dashed rgba($color: #000, $alpha: .5);
        }
      }
    }
  }
}
</style>
